-- Run on mangos DB-- Drop unused table:DROP TABLE IF EXISTS creature_movement_addon;-- Alter movement to add run flag:ALTER TABLE creature_movementADD `run` TINYINT(3) UNSIGNED NOT NULL DEFAULT 0;-- Alter movement template to add run flag:ALTER TABLE creature_movement_templateADD `run` TINYINT(3) UNSIGNED NOT NULL DEFAULT 0;UPDATE command SET help="Syntax: .wp modify [#creature_guid or Select a Creature]add - Add a waypoint after the selected visualwaittime $timeemote IDspell IDtext1| text2| text3| text4| text5 <text>model1 IDmodel2 IDmove(moves wp to player pos)del (deletes the wp)run - 1 to run, 0 to walkOnly one parameter per time!" WHERE name="wp modify";